import React,{Component} from 'react'
import Swiper from 'react-id-swiper';
import{
    Link
}from 'react-router-dom';
import './RelatedImages.css'
export default class RelatedImages extends Component{
    render(){
        /*const params = {
            effect:'coverflow',
            grabCursor:true,
            centeredSlides:true,
            slidesPerView:'auto',
            coverflowEffect:{
                rotate:50,
                stretch:0,
                depth:100,
                slideShadow:true
            },
            pagination:{
                el:'.swiper-pagination'
            }
        }*/

        const params = {
            pagination: {
              el: '.swiper-pagination',
              type: 'progressbar',
            }
          };
        return(
            <div className="RelatedImages">
                <Swiper {...params}>
                    <div style={{height:"400px"}}> <Link to="/posts/1"> data1</Link></div>
                    <div style={{height:"400px"}}> <Link to="/posts/2"> data2</Link></div>
                    <div style={{height:"400px"}}> <Link to="/posts/3"> data3</Link></div>
                    <div style={{height:"400px"}}> <Link to="/posts/4"> data4</Link></div>
                    <div style={{height:"400px"}}> <Link to="/posts/5"> data5</Link></div>
                </Swiper>
            </div>
            
        )
    }
}
