import Swiper from 'react-id-swiper';
import {
    Link
} from 'react-router-dom'
import './RelatedPosts.css'
export default class RelatedPosts extends Component{
    render(){
        /*const params = {
            effect:'coverflow',
            grabCursor:true,
            centeredSlides:true,
            slidesPerView:'auto',
            coverflowEffect:{
                rotate:50,
                stretch:0,
                depth:100,
                slideShadow:true
            },
            pagination:{
                el:'.swiper-pagination'
            }
        } */
        const params = {
            pagination: {
              el: '.swiper-pagination',
              type: 'progressbar',
            }
          };

        return(
            <div className="relatedPosts">
            
                    <div> <Link to="/posts/1"> data1</Link></div>
                    <div> <Link to="/posts/2"> data2</Link></div>
                    <div> <Link to="/posts/3"> data3</Link></div>
                    <div> <Link to="/posts/4"> data4</Link></div>
                    <div> <Link to="/posts/5"> data5</Link></div>
            </div>
        )
    }
}
