import React,{Component} from 'react'
import Swiper from 'react-id-swiper';
import{
    Link
}from 'react-router-dom';
import './RelatedImages.css'
export default class RelatedImages extends Component{
    render(){
        const params = {
            effect:'coverflow',
            grabCursor:true,
            centeredSlides:true,
            slidesPerView:'auto',
            coverflowEffect:{
                rotate:50,
                stretch:0,
                depth:100,
                slideShadow:true
            },
            pagination:{
                el:'.swiper-pagination'
            }
        }

         /* const params={
            pagination: {
                el: '.swiper-pagination',
                clickable: true,
                dynamicBullets: true
              }
          }*/
        return(
            <div className="RelatedImages">
                <Swiper {...params}>
                    <div style={{height:"400px"}}> <Link to="/posts:1"> <img src="http://www.hdnicewallpapers.com/Walls/Big/Others/Lord_of_The_Rings_Map_Wallpaper.jpg" alt="main" className="related_images"/></Link></div>
                    <div style={{height:"400px"}}> <Link to="/posts:2"> <img src="http://www.hdnicewallpapers.com/Walls/Big/Others/Lord_of_The_Rings_Map_Wallpaper.jpg" alt="main" className="related_images"/></Link></div>
                    <div style={{height:"400px"}}> <Link to="/posts:3"> <img src="http://www.hdnicewallpapers.com/Walls/Big/Others/Lord_of_The_Rings_Map_Wallpaper.jpg" alt="main" className="related_images"/></Link></div>
                    <div style={{height:"400px"}}> <Link to="/posts:4"> <img src="http://www.hdnicewallpapers.com/Walls/Big/Others/Lord_of_The_Rings_Map_Wallpaper.jpg" alt="main" className="related_images"/></Link></div>
                    <div style={{height:"400px"}}> <Link to="/posts:5"> <img src="http://www.hdnicewallpapers.com/Walls/Big/Others/Lord_of_The_Rings_Map_Wallpaper.jpg" alt="main" className="related_images"/></Link></div>
                </Swiper>
            </div>
            
        )
    }
}
